import { createAsyncThunk } from "@reduxjs/toolkit";

import { toast } from "react-toastify";
import { fetchWeather, fetchWeatherDataByCity } from "../../service/api";

export const getWeatherThunk = createAsyncThunk(
  "weather/weatherThunk",
  async (query, thunkAPI) => {
    try {
      const response = await fetchWeather(query);
      return response;
    } catch (error) {
      return thunkAPI.rejectWithValue(error.message);
    }
  }
);

export const getWeatherByCity = createAsyncThunk(
  "weather/getWeatherByCity",
  async (city, thunkAPI) => {
    try {
      const data = await fetchWeatherDataByCity(city);
      return data;
    } catch (error) {
      toast.error("Incorrect data");
      return thunkAPI.rejectWithValue(error.message);
    }
  }
);
