import React, { useEffect, useState, useRef } from "react";
import Select from "react-select";

import sprite from "../../images/sprite.svg";
import { HeaderWrapper, StyledSvg } from "./Header.styled";
import { useTranslation } from "react-i18next";

const customStyles = {
  menu: (provided) => ({
    ...provided,
    width: "72px",
  }),
  control: (provided, state) => ({
    ...provided,
    padding: "0px",
    width: "50px",
    border: "none",
    boxShadow: state.isFocused ? null : "none",
  }),
  indicatorSeparator: () => ({ display: "none" }),
  option: (provided) => ({
    ...provided,
    width: "72px",
  }),
  dropdownIndicator: (provided, state) => ({
    ...provided,
    transform: state.selectProps.menuIsOpen ? "rotate(180deg)" : null,
    transition: "transform 0.3s ease-in-out",
    padding: "0px",
  }),
  singleValue: (provided) => ({
    ...provided,
    color: "#AFAFAF",
    width: "40px",
  }),
  valueContainer: (provided) => ({
    ...provided,
    padding: "0px",
  }),
};

const LANGUAGES = [
  { value: "en", label: "EN" },
  { value: "ua", label: "UA" },
  { value: "he", label: "HE" },
];

const STORAGE_KEY = "i18next_lng";

const getDefaultLanguage = () => {
  const userLanguage = (navigator.language || navigator.userLanguage).split(
    "-"
  )[0];
  return LANGUAGES.find((lang) => lang.value === userLanguage) || LANGUAGES[0];
};

const Header = ({ defaultLanguage = getDefaultLanguage() }) => {
  const { i18n } = useTranslation();
  const [selectedLanguage, setSelectedLanguage] = useState(null);
  const localStorageRef = useRef(localStorage);

  useEffect(() => {
    const languageFromStorage = localStorageRef.current.getItem(STORAGE_KEY);
    setSelectedLanguage(
      languageFromStorage
        ? {
            value: languageFromStorage,
            label: languageFromStorage.toUpperCase(),
          }
        : defaultLanguage
    );
  }, [defaultLanguage]);

  useEffect(() => {
    if (selectedLanguage) {
      i18n
        .changeLanguage(selectedLanguage.value)
        .catch((error) => console.error("Failed to change language:", error));
      localStorageRef.current
        .setItem(STORAGE_KEY, selectedLanguage.value)
        .catch((error) =>
          console.error("Failed to save language to Local Storage:", error)
        );
    }
  }, [selectedLanguage, i18n]);

  const handleChangeLanguage = (selectedOption) => {
    setSelectedLanguage(selectedOption);
  };

  return (
    <HeaderWrapper>
      <StyledSvg>
        <use href={`${sprite}#globe`} />
      </StyledSvg>
      <Select
        options={LANGUAGES}
        onChange={handleChangeLanguage}
        value={selectedLanguage}
        styles={customStyles}
      />
    </HeaderWrapper>
  );
};

export default Header;
