        // September: "September",
        // October: "October",
        // November: "November",
        // December: "December",
      },
    },
  },
  ua: {
    translation: {
      feels_like: "Відчувається як",
      wind: "Вітер",
      humidity: "Вологість",
      pressure: "Тиск",
      add: "Додати",
      Sunny: "Ясно",
      Clouds: "Хмарно",
      Rain: "Дощ",
      selectLanguage: "Select Language:",
      shortDays: {
        Mon: "пн",
        Tue: "вт",
        Wed: "ср",
        Thu: "чт",
        Fri: "пт",
        Sat: "сб",
        Sun: "нд",
      },
      longMonths: {
        January: "Січ",
        February: "Лют",
        March: "Бер",
        April: "Кві",
        May: "Тра",
        June: "Чер",
        July: "Лип",
        August: "Сер",
        September: "Вер",
        October: "Жов",
        November: "Лис",
        December: "Гру",
      },
    },
  },
  he: {
    translation: {
      feels_like: "נראה כמו",
      wind: "רוח",
      humidity: "לחות",
      pressure: "לחץ",
      add: "הוסף",
      Sunny: "שמשי",
      Clouds: "מעונן",
      Rain: "גשם",
      selectLanguage: "Select Language:",
      shortDays: {
        Mon: "ב׳",
        Tue: "ג׳",
        Wed: "ד׳",
        Thu: "ה׳",
        Fri: "ו׳",
        Sat: "ש׳",
        Sun: "א׳",
      },
      shortMonths: {
        January: "ינו׳",
        February: "פבר׳",
        March: "מרץ",
        April: "אפר׳",
        May: "מאי",
        June: "יוני",
        July: "יולי",
        August: "אוג׳",
        September: "ספט׳",
        October: "אוק׳",
        November: "נוב׳",
        December: "דצמ׳",
      },
    },
  },
};

i18n
  .use(initReactI18next)
  .use(LanguageDetector)
  .use(LocalStorageBackend)
  .init({
    resources,
    lng: localStorage.getItem("i18next_lng") || "en",
    fallbackLng: "en",
    interpolation: {
      escapeValue: false,
    },
    backend: {
      keySeparator: false,
      prefix: "i18next_",
    },
  });

export default i18n;
