import React from "react";

const Translation = ({ language }) => {
  const translations = {
    en: {
      label: "EN",
      placeholder: "Search...",
    },
    ua: {
      label: "UA",
      placeholder: "Пошук...",
    },
    he: {
      label: "HE",
      placeholder: "חיפוש...",
    },
  };

  const { label, placeholder } = translations[language] || translations["en"];

  return (
    <div>
      <span>{label}</span>
      <input type="text" placeholder={placeholder} />
    </div>
  );
};

export default Translation;
