import React from "react";

const Translation = ({ language }) => {
  const translations = {
    en: {
      label: "EN",
      placeholder: "Search...",
      feels_like: "Feels like",
    },
    ua: {
      label: "UA",
      placeholder: "Пошук...",
      feels_like: "נראה כמו",
    },
    he: {
      label: "HE",
      placeholder: "חיפוש...",
      feels_like: "Відчувається як",
    },
  };

  return translations[language] || translations["en"];
};

export default Translation;
