// export default App;

const App = () => {
  const [cities, setCities] = useState([]);
  const favorites = useSelector(selectFavorites);
  const [weatherData, setWeatherData] = useState(null);
  const [inputWeatherData, setInputWeatherData] = useState(null); // Данные о погоде из инпута

  useEffect(() => {
    currentLocationWeather();
  }, []);

  const currentLocationWeather = () => {
    navigator.geolocation.getCurrentPosition(
      async (position) => {
        const { latitude, longitude } = position.coords;
        try {
          const response = await axios.get(
            `https://api.openweathermap.org/data/2.5/weather`,
            {
              params: {
                lat: latitude,
                lon: longitude,
                appid: "18431911169fef4afbe92c0ef62b8409",
                units: "metric",
              },
            }
          );
          const weatherData = response.data;
          setWeatherData(weatherData);
        } catch (error) {
          console.error("Error fetching weather data:", error);
          // Если произошла ошибка при получении данных о геопозиции, отображаем данные из инпута
          if (inputWeatherData) {
            setWeatherData(inputWeatherData);
          }
        }
      },
      (error) => {
        console.error("Error getting current location:", error);
        // Если произошла ошибка при получении данных о геопозиции, отображаем данные из инпута
        if (inputWeatherData) {
          setWeatherData(inputWeatherData);
        }
      }
    );
  };

  return (
    <I18nextProvider i18n={i18n}>
      <div className="container">
        <Header />
        <SearchBar
          cities={cities}
          setCities={setCities}
          addToFavorites={addToFavorites}
          // Callback для передачи данных о погоде из инпута в App
          setInputWeatherData={setInputWeatherData}
        />
        {weatherData && <WeatherCard weatherData={weatherData} />}
        {favorites.length > 0 && <WeatherList cities={cities} />}
      </div>
    </I18nextProvider>
  );
};

