import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import LocalStorageBackend from "i18next-localstorage-backend";

const resources = {
  en: {
    translation: {
      feels_like: "Feels like",
      wind: "Wind",
      humidity: "Humidity",
      pressure: "Pressure",
      add: "Add",
      Sunny: "Sunny",
      Clouds: "Clouds",
      Rain: "Rain",
      selectLanguage: "Select Language:",
      short_day: {
        Mon: "Mon",
        Tue: "Tue",
        Wed: "Wed",
        Thu: "Thu",
        Fri: "Fri",
        Sat: "Sat",
        Sun: "Sun",
        // January: "January",
        // February: "February",
        // March: "March",
        // April: "April",
        // May: "May",
        // June: "June",
        // July: "July",
        // August: "August",
        // September: "September",
        // October: "October",
        // November: "November",
        // December: "December",
      },
    },
  },
  ua: {
    translation: {
      feels_like: "Відчувається як",
      wind: "Вітер",
      humidity: "Вологість",
      pressure: "Тиск",
      add: "Додати",
      Sunny: "Ясно",
      Clouds: "Хмарно",
      Rain: "Дощ",
      selectLanguage: "Select Language:",
      Mon: "пн",
      Tue: "вт",
      Wed: "ср",
      Thu: "чт",
      Fri: "пт",
      Sat: "сб",
      Sun: "нд",
      // Janu: "січня",
      // February: "лютого",
      // March: "березня",
      // April: "квітня",
      // May: "травня",
      // June: "червня",
      // July: "липня",
      // August: "серпня",
      // September: "вересня",
      // October: "жовтня",
      // November: "листопада",
      // December: "грудня",
    },
  },
  he: {
    translation: {
      feels_like: "נראה כמו",
      wind: "רוח",
      humidity: "לחות",
      pressure: "לחץ",
      add: "הוסף",
      Sunny: "שמשי",
      Clouds: "מעונן",
      Rain: "גשם",
      selectLanguage: "Select Language:",
    },
  },
};

i18n
  .use(initReactI18next)
  .use(LanguageDetector)
  .use(LocalStorageBackend)
  .init({
    resources,
    lng: localStorage.getItem("i18next_lng") || "en",
    fallbackLng: "en",
    interpolation: {
      escapeValue: false,
    },
    backend: {
      keySeparator: false,
      prefix: "i18next_",
    },
  });

export default i18n;
