// WeatherList.js
import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { selectFavorites } from "../redux/selectors";
import sprite from "../../images/sprite.svg";
import {
  FavoriteWrapper,
  ListWrapper,
  StyledSvgDiv,
  SvgCross,
  WrapperSvg,
} from "./WeatherList.styles";
import { removeFromFavorites } from "../redux/slice";
import { nanoid } from "nanoid";
import {
  DateParagraph,
  DegSpan,
  DegSwitcherDiv,
  Img,
  MainWeatherInfoP,
  OrangeSpan,
  ParagraphSun,
  Span,
  StyledChartDiv,
  StyledDegreeBtn,
  StyledFeelsPar,
  StyledImgSunDiv,
  StyledTemperature,
  StyledTitleWrapper,
  StyledWeatherInfoDiv,
  TemperatureDiv,
  TitleH2,
  WeatherCardWrapper,
  WeatherInfoDiv,
} from "./WeatherCard.styled";
import moment from "moment";
import { useTranslation } from "react-i18next";
import Chart from "../Chart/Chart";

const WeatherList = ({ weatherData }) => {
  const { t } = useTranslation();

  const favoriteWeather = useSelector(selectFavorites);
  const dispatch = useDispatch();
  const [unit, setUnit] = useState("celsius");

  const icon = favoriteWeather[0].list[0].weather[0].icon;
  const iconUrl = `http://openweathermap.org/img/wn/${icon}.png`;

  const weatherStatus = favoriteWeather[0].list[0].weather[0].main;

  const sunnyWeatherStatus =
    weatherStatus === "Clear" ? t("Sunny") : t(weatherStatus);

  // const formatDate = (dateString) => {
  //   const date = moment(dateString);
  //   const formattedDate = date.format("ddd, D MMMM, HH:mm");

  //   return formattedDate;
  // };

  const formatDate = (dateString) => {
    const date = moment(dateString);
    const formattedDate = date.format("ddd, D MMMM, HH:mm");

    const shortDay = date.format("ddd");
    const longMonth = date.format("MMMM");

    return (
      t("shortDays." + shortDay) +
      ", " +
      date.format("D") +
      " " +
      t("longMonths." + longMonth) +
      ", " +
      date.format("HH:mm")
    );
  };

  const originalDate = favoriteWeather[0].list[0].dt_txt;
  const formattedDate = formatDate(originalDate);

  const convertTemperature = (value) => {
    if (unit === "celsius") {
      return value;
    } else {
      return Math.round((value * 9) / 5 + 32);
    }
  };

  const handleRemoveItem = (index) => {
    dispatch(removeFromFavorites(index));
  };

  if (favoriteWeather.length === 0) {
    return <div>No favorite weather data available</div>;
  }

  const chartDataArray = favoriteWeather.map((weather) => weather.list);

  return (
    <FavoriteWrapper>
      {favoriteWeather.map((favorite, index) => {
        const cardBackgroundColor =
          Math.round(convertTemperature(favorite.list[0].main.temp)) < 0
            ? "#F1F2FF"
            : "#FFFAF1";
        const spanColor =
          Math.round(convertTemperature(favorite.list[0].main.temp)) < 0
            ? "#459DE9"
            : "#FFA25B";
        return (
          <WeatherCardWrapper key={index} backgroundColor={cardBackgroundColor}>
            <StyledSvgDiv>
              <SvgCross onClick={() => handleRemoveItem(index)}>
                <use href={`${sprite}#icon-cross`}></use>
              </SvgCross>
            </StyledSvgDiv>
            <StyledTitleWrapper>
              <TitleH2>
                {favorite.city.name}, {favorite.city.country}
              </TitleH2>
              <StyledImgSunDiv>
                <Img src={iconUrl} alt="sun" />
                <ParagraphSun>{sunnyWeatherStatus}</ParagraphSun>
              </StyledImgSunDiv>
            </StyledTitleWrapper>
            {/* <DateParagraph>{formattedDate}</DateParagraph> */}
            <DateParagraph>{t(formattedDate)}</DateParagraph>

            {/* <StyledChartDiv>
              <Chart data={favoriteWeather.list} />
            </StyledChartDiv> */}

            <StyledChartDiv>
              <Chart data={chartDataArray[index]} />
            </StyledChartDiv>

            <StyledWeatherInfoDiv>
              <TemperatureDiv>
                {/* <StyledTemperature>
                  {Math.round(convertTemperature(favorite.list[0].main.temp))}
                </StyledTemperature> */}

                {/* <StyledTemperature>
                  {convertTemperature(favorite.list[0].main.temp) > 0
                    ? "+"
                    : ""}
                  {Math.round(convertTemperature(favorite.list[0].main.temp))}
                </StyledTemperature> */}

                <StyledFeelsPar>
                  {t("feels_like")}:&nbsp;
                  <DegSpan>
                    {Math.round(
                      convertTemperature(favorite.list[0].main.feels_like)
                    )}
                    &nbsp;
                    {unit === "celsius" ? "°C" : "°F"}
                  </DegSpan>
                </StyledFeelsPar>

                <DegSwitcherDiv>
                  <StyledDegreeBtn
                    onClick={() => setUnit("celsius")}
                    $active={unit === "celsius"}
                  >
                    °C
                  </StyledDegreeBtn>
                  <StyledDegreeBtn
                    onClick={() => setUnit("fahrenheit")}
                    $active={unit === "fahrenheit"}
                  >
                    °F
                  </StyledDegreeBtn>
                </DegSwitcherDiv>
              </TemperatureDiv>

              <WeatherInfoDiv>
                <MainWeatherInfoP>
                  {t("wind")}: &nbsp;
                  <OrangeSpan color={spanColor}>
                    {Math.round(favorite.list[0].wind.speed)}&nbsp;m/s
                  </OrangeSpan>
                </MainWeatherInfoP>
                <MainWeatherInfoP>
                  {t("humidity")}: &nbsp;
                  <OrangeSpan color={spanColor}>
                    {favorite.list[0].main.humidity}%
                  </OrangeSpan>
                </MainWeatherInfoP>
                <MainWeatherInfoP>
                  {t("pressure")}: &nbsp;
                  <OrangeSpan color={spanColor}>
                    {favorite.list[0].main.pressure}Pa
                  </OrangeSpan>
                </MainWeatherInfoP>
              </WeatherInfoDiv>
            </StyledWeatherInfoDiv>
          </WeatherCardWrapper>
        );
      })}
    </FavoriteWrapper>
  );
};

export default WeatherList;
