const Translation = ({ language }) => {
  const translations = {
    en: {
      label: "EN",
      placeholder: "Search...",
      feels_like: "Feels like",
    },
    ua: {
      label: "UA",
      placeholder: "Пошук...",
      feels_like: "Насправді",
    },
    he: {
      label: "HE",
      placeholder: "חיפוש...",
      feels_like: "מרגיש כמו",
    },
  };

  // Возвращаем только конкретную строку перевода
  return translations[language]?.feels_like || translations["en"].feels_like;
};
