import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getWeatherThunk } from "../redux/thunksAPI";
import WeatherCard from "../Weather/WeatherCard";
import { addToFavorites } from "../redux/slice";
import { selectWeather } from "../redux/selectors";
import { nanoid } from "nanoid";
import Select from "react-select";

const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedCity, setSelectedCity] = useState(null);

  const dispatch = useDispatch();
  const weatherData = useSelector(selectWeather);

  useEffect(() => {
    const fetchWeather = async () => {
      if (searchTerm.trim() !== "") {
        try {
          await dispatch(getWeatherThunk(searchTerm));
        } catch (error) {
          console.error("Error fetching weather data:", error);
        }
      }
    };

    fetchWeather();
  }, [dispatch, searchTerm]);

  const handleInputChange = async (value) => {
    setSearchTerm(value);
    if (value.trim() === "") {
      setSearchResults([]);
      return;
    }
    setLoading(true);
    try {
      const response = await fetch(
        `https://api.openweathermap.org/geo/1.0/direct?q=${value}&limit=5&appid=18431911169fef4afbe92c0ef62b8409`
      );
      const data = await response.json();
      setSearchResults(data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
    setLoading(false);
  };

  const customStyles = {
    input: (provided) => ({
      ...provided,
      borderRadius: "4px",
      border: "1px solid #ced4da",
      padding: "8px",
    }),
  };

  const handleCitySelect = (city) => {
    setSelectedCity(city);
    setSearchResults([]);
    setSearchTerm("");
  };

  const handleAddClick = () => {
    if (selectedCity) {
      dispatch(addToFavorites(weatherData));
      setSelectedCity(null);
    }
  };

  return (
    <div>
      <Select
        value={searchTerm}
        onChange={(selectedOption) => handleInputChange(selectedOption.value)}
        onInputChange={(inputValue) => handleInputChange(inputValue)}
        options={searchResults.map((city) => ({
          value: city.name,
          label: city.name,
        }))}
        isLoading={loading}
        placeholder="Type a city name..."
        styles={customStyles}
      />
      <div>
        {selectedCity && (
          <WeatherCard
            key={nanoid()}
            city={selectedCity.name}
            temperature={selectedCity.temperature}
            wind={selectedCity.wind}
            humidity={selectedCity.humidity}
          />
        )}
        <button onClick={handleAddClick}>Add</button>
      </div>

      <div>
        <ul>
          {selectedCity &&
            weatherData?.list
              ?.slice(0, 1)
              .map((weatherDataItem) => (
                <WeatherCard key={nanoid()} weatherData={weatherDataItem} />
              ))}
        </ul>
      </div>
    </div>
  );
};

export default SearchBar;
